import React, { useState, useEffect } from "react";
import CountriesData from "react-select-country-list";
import "nativewind";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  FlatList,
  Alert,
} from "react-native";

const App = () => {
  const [description, setDesc] = useState("");
  const [assigny, setAssigny] = useState("");
  const [country, setCountry] = useState("");
  const [tasks, setTasks] = useState(
    JSON.parse(localStorage.getItem("tasks")) || [],
  );
  const [editIndex, setEditIndex] = useState(-1);
  const countries = CountriesData()
    .getData()
    .map((a) => a.label);

  useEffect(() => {
    const storageData = localStorage.getItem("tasks");
    if (storageData) {
      let parsedData = JSON.parse(storageData);
      if (parsedData.assigny) {
        setTasks([
          ...tasks,
          {
            assigny: parsedData.assigny,
            desc: parsedData.desc,
            country: parsedData.country,
          },
        ]);
      }
    }
  }, []);

  const Dropdown = ({ placeholder, data, selectedValue, onSelect }) => {
    const [visible, setVisible] = useState(false);

    const handleSelect = (item) => {
      onSelect(item);
      setVisible(false);
    };

    return (
      <View className="relative w-full max-w-xs mb-2">
        <TouchableOpacity
          className="p-3 bg-gray-200 border border-gray-400 rounded-md text-center"
          onPress={() => setVisible(!visible)}
        >
          <Text>{selectedValue || placeholder}</Text>
        </TouchableOpacity>
        {visible && (
          <View className="absolute top-full left-0 right-0 bg-white border border-gray-400 rounded-md mt-1 max-h-40 overflow-hidden">
            <FlatList
              data={data}
              keyExtractor={(item, index) => index.toString()}
              renderItem={({ item }) => (
                <TouchableOpacity
                  className={`p-2 border-b border-gray-300 ${
                    item === selectedValue ? "bg-blue-100" : ""
                  }`}
                  onPress={() => handleSelect(item)}
                >
                  <Text className="text-gray-800">{item}</Text>
                </TouchableOpacity>
              )}
            />
          </View>
        )}
      </View>
    );
  };

  const handleAddTask = () => {
    if (assigny === "") {
      alert("Must assign task to someone!");
      return;
    } else if (country === "") {
      alert("Must select a country!");
      return;
    } else if (description.length > 250) {
      alert("Description must be 250 characters or less!");
      return;
    } else if (description.length < 1) {
      alert("Must give a description!");
      return;
    }

    const updatedTasks =
      editIndex !== -1
        ? tasks.map((task, index) =>
            index === editIndex
              ? { assigny, desc: description, country }
              : task,
          )
        : [...tasks, { assigny, desc: description, country }];

    localStorage.setItem("tasks", JSON.stringify(updatedTasks));
    setTasks(updatedTasks);
    setAssigny("");
    setDesc("");
    setCountry("");
    setEditIndex(-1);
  };

  const handleEditTask = (index) => {
    const taskToEdit = tasks[index];
    setAssigny(taskToEdit.assigny);
    setDesc(taskToEdit.desc);
    setCountry(taskToEdit.country);
    setEditIndex(index);
  };

  const handleDeleteTask = (index) => {
    Alert.alert("Delete Task", "Are you sure you want to delete this task?", [
      { text: "Cancel", style: "cancel" },
      {
        text: "OK",
        onPress: () => {
          const updatedTasks = tasks.filter((_, i) => i !== index);
          localStorage.setItem("tasks", JSON.stringify(updatedTasks));
          setTasks(updatedTasks);
        },
      },
    ]);
  };

  const renderItem = ({ item, index }) => (
    <View className="flex flex-row justify-between items-center mb-3">
      <Text className="text-lg">{item.assigny}</Text>
      <Text className="text-lg">{item.desc}</Text>
      <Text className="text-lg">{item.country}</Text>
      <View className="flex flex-row">
        <TouchableOpacity onPress={() => handleEditTask(index)}>
          <Text className="mr-2 text-blue-500 font-bold">Edit</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => handleDeleteTask(index)}>
          <Text className="text-red-500 font-bold">Delete</Text>
        </TouchableOpacity>
      </View>
    </View>
  );

  return (
    <View className="flex-1 p-10 bg-white">
      <Text className="text-2xl font-bold mb-4">FJNCH</Text>
      <Text className="text-xl font-bold mb-4">ToDo List</Text>
      <TextInput
        className="border border-gray-300 p-2 rounded-md mb-2 text-lg"
        placeholder="Assigny"
        value={assigny}
        onChangeText={setAssigny}
      />
      <Dropdown
        placeholder="Country"
        data={countries}
        selectedValue={country}
        onSelect={setCountry}
      />
      <TextInput
        maxLength={250}
        className="border border-gray-300 p-2 rounded-md mb-2 h-40 text-lg"
        placeholder="Description"
        value={description}
        onChangeText={setDesc}
      />
      <TouchableOpacity
        className="bg-blue-500 p-3 rounded-md mb-4"
        onPress={handleAddTask}
      >
        <Text className="text-white text-lg text-center">
          {editIndex !== -1 ? "Update Task" : "Add Task"}
        </Text>
      </TouchableOpacity>
      <FlatList
        data={tasks}
        renderItem={renderItem}
        keyExtractor={(item, index) => index.toString()}
      />
    </View>
  );
};

export default App;
